mapper.select:

	select u.id,u.username, b.userno, b.blogtitle, b.logofile
	from blog b, users u
	where u.userno=b.userno and u.id=#{id}

mapper.update:

	update blog set
	blogTitle= #{blogTitle}
	where userno=#{userNo}


	update blog set
	logofile = #{logoFile}, blogTitle=#{blogTitle}
	where userno=#{userNo}
